import { useEffect } from "react";
import { url_header } from "../utils/url_header";
import { useDispatch, useSelector } from "react-redux";
import { addNowPlayingMovie } from "../utils/movieSlice";

const VideoBackground = () =>{
    const dispatch = useDispatch();
    const temp = useSelector(store=>store.movies?.nowPlayingMovies)
    //console.log(key)
    const movies = async () =>{
        const data = await fetch('https://api.themoviedb.org/3/movie/1096197/videos?language=en-US', url_header)

        const json = await data.json();
        const res = (!json) ? null : json.results;
        const video = res.filter(item => item.type === "Trailer");
        const key = video[0]?.key;
        dispatch(addNowPlayingMovie(key));
        //console.log(video[0]?.key);
        //dispatch(addNowPlayingMovie(res))
    }
    useEffect(()=>{movies()},[]);
    console.log(temp.key);
    return(
        <div className=" pt-[80] w-screen">
            <iframe className="w-screen aspect-video" src={"https://www.youtube.com/embed/"+temp.key} title="YouTube video player"  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" ></iframe>
            </div>
    )
}
export default VideoBackground;
