import { useEffect } from "react";
import { url_header } from "../utils/url_header";
import { useDispatch, useSelector } from "react-redux";
import { addNowPlayingMovie, addTrailerVideo } from "../utils/movieSlice";

const useMainMovie = (movieId) =>{
    const temp = useSelector(store=> store.movies)
    const dispatch = useDispatch();
    const movies = async (id) =>{

        console.log(id);
        const data = await fetch("https://api.themoviedb.org/3/movie/videos?language=en-US", url_header)
        const json = await data.json();
        const filterData = json?.results.filter((video)=> video.type === "Trailer")
        const trailer = filterData.length ? filterData[0] : json.results[0];
        const res = (!json) ? null : json.results;

        dispatch(addTrailerVideo(trailer));
        //console.log(video[0]?.key);
        //dispatch(addNowPlayingMovie(res))
    }
    useEffect(()=>{movies(movieId)},[]);
    return(
        <>
        </>
    )
}
export default useMainMovie;
