import { useDispatch, useSelector } from "react-redux";
import VideoBackground from "./VideoBackground";
import VideoTitle from "./VideoTitle";
import { useEffect } from "react";
import { addNowPlayingMovie, addTrailerVideo } from "../utils/movieSlice";

const MainContainer = () =>{
    const movies  = useSelector(store => store.movies?.nowPlayingMovies)

    //console.log(movies[0]?.id);
    const dispatch = useDispatch();
    // useEffect(()=>{
    //     dispatch(addNowPlayingMovie(movies[0]?.title));
    // },[]);
    if(!movies){return;}
    const mainMovie = movies[0];

    return(
        <div className="absolute">
            <VideoBackground movieId={mainMovie?.id} className="w-screen"/>
            <VideoTitle className="text-white relative" title={mainMovie?.title} description={mainMovie?.overview}/>
        </div>
    );
}
export default MainContainer;
