const VideoTitle = ({title,description}) =>{
    return(
        <div className=" pt-32  aspect-video absolute bg-gradient-to-r from-black">
            <div className="w-1/2">
            <h1 className="text-4xl font-bold text-white">{title}</h1>
            <p className="pt-4 text-white">{description}</p>
            <button className="bg-gray-800 m-2 px-2 py-1 rounded-md text-white text-2xl">
                Play
            </button>
            <button>More info</button>
            </div>

        </div>
    )
}
export default VideoTitle;
